        "icon-xs": "h-8 w-8",
        "icon-sm": "h-9 w-9",
        icon: "h-10 w-10",
        "icon-lg": "h-11 w-11",
      },
      busy: { true: "relative !cursor-wait !pointer-events-none" }
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
);

export type ButtonProps = VariantProps<typeof buttonVariants>;

@Component({
  selector: 'tolle-button',
  standalone: true,
  imports: [CommonModule],
  template: `
    <button
      [class]="computedClass"
      [disabled]="disabled || busy"
      [attr.aria-busy]="busy"
    >
      <div *ngIf="busy" class="absolute inset-0 flex items-center justify-center">
        <svg class="animate-spin h-5 w-5 text-current" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
          <circle class="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" stroke-width="4"></circle>
          <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
        </svg>
      </div>

      <span class="flex items-center justify-center w-full h-full pointer-events-none" [class.invisible]="busy">
        <ng-content></ng-content>
      </span>
    </button>
  `,
  styles: [`
    :host {
      display: inline-block;
      vertical-align: middle;
    }

    :host ::ng-deep i {
      display: inline-flex;
      align-items: center;
      justify-content: center;
      line-height: 1;
    }

    :host-context(.size-xs) ::ng-deep i,
    :host-context(.size-icon-xs) ::ng-deep i,
    :host-context(.size-sm) ::ng-deep i,
    :host-context(.size-icon-sm) ::ng-deep i {
      font-size: 1rem;
    }

    :host-context(.size-default) ::ng-deep i,
    :host-context(.size-icon) ::ng-deep i,
    :host-context(.size-lg) ::ng-deep i,
    :host-context(.size-icon-lg) ::ng-deep i {
      font-size: 1.2rem;
    }
  `]
})
export class ButtonComponent {
  @Input() class: string = '';
  @Input() variant: ButtonProps['variant'] = 'default';
  @Input() size: ButtonProps['size'] = 'default';
  @Input() disabled: boolean = false;
  @Input() busy: boolean = false;
  @Input() readonly: boolean = false;

  get computedClass() {
    return cn(
      buttonVariants({
        variant: this.variant,
        size: this.size,
        busy: this.busy
      }),
      'size-' + this.size,
      this.class
    );
  }
}

