@Component({
  selector: 'tolle-resizable-handle',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div
      (mousedown)="onMouseDown($event)"
      [class]="cn(
        'relative flex items-center justify-center bg-border transition-all hover:bg-primary z-10',
        'after:absolute after:inset-y-0 after:left-1/2 after:w-4 after:-translate-x-1/2',
        group.direction === 'horizontal' ? 'w-px h-full cursor-col-resize' : 'h-px w-full cursor-row-resize',
        class
      )"
    >
      <div *ngIf="withHandle" class="z-20 flex h-4 w-3 items-center justify-center rounded-sm border bg-border shadow-sm">
        <i class="ri-more-2-fill text-[10px]" [class.rotate-90]="group.direction === 'horizontal'"></i>
      </div>
    </div>
  `
})
export class ResizableHandleComponent {
  @Input() index = 0;
  @Input() withHandle = false;
  @Input() class = '';

  constructor(protected group: ResizablePanelGroupComponent) {}

  onMouseDown(event: MouseEvent) {
    // 1. Stop text selection and bubbling
    event.preventDefault();
    event.stopPropagation();

    // 2. Initialize the drag index
    this.group.startResizing(this.index);

    // 3. IMPORTANT: Manually trigger the group's mousedown to capture Initial Mouse Position
    // Without this, the delta calculation is wrong and the panel jumps.
    this.group.onMouseDown(event);
  }

  protected cn = cn;
}

import { Component, Input, Output, EventEmitter } from '@angular/core';
import { cn } from './utils/cn';

@Component({
  selector: 'tolle-resizable-panel',
  standalone: true,
  template: `
    <div
      [style.flex]="(isCollapsed ? collapsedSize : size) + ' 1 0px'"
      [class]="cn(
        'overflow-hidden',
        !isDragging ? 'transition-all duration-300 ease-in-out' : '',
        class
      )"
    >
      <ng-content></ng-content>
    </div>
  `
})
export class ResizablePanelComponent {
  @Input() size = 0;
  @Input() minSize = 10;
  @Input() collapsible = false;
  @Input() collapsedSize = 0;
  @Input() class = '';
  @Input() isDragging = false;
  @Output() onCollapse = new EventEmitter<boolean>();

  isCollapsed = false;

  updateSize(newSize: number) {
    if (this.collapsible) {
      const collapseThreshold = this.minSize * 0.25;

      // 1. If we are currently visible and drag way below the threshold -> Collapse
      if (newSize < collapseThreshold && !this.isCollapsed) {
        this.isCollapsed = true;
        this.size = this.collapsedSize;
        this.onCollapse.emit(true);
        return;
      }

      // 2. If we are collapsed and drag past 50% of minSize -> Expand
      if (this.isCollapsed && newSize > this.minSize * 0.5) {
        this.isCollapsed = false;
        this.onCollapse.emit(false);
      }
    }

    // 3. Clamping: If not collapsed, force the size to be at least minSize
    // This prevents the panel from visually shrinking below minSize
    // until the snap threshold is actually hit.
    if (!this.isCollapsed) {
      this.size = Math.max(this.minSize, newSize);
    }
  }

  protected cn = cn;
}

import { Component, Input, ElementRef, ContentChildren, QueryList, AfterContentInit, HostListener } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ResizablePanelComponent } from './resizable-panel.component';
import { cn } from './utils/cn';

@Component({
  selector: 'tolle-resizable-panel-group',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div [class]="cn('flex h-full w-full', direction === 'vertical' ? 'flex-col' : 'flex-row', class)">
      <ng-content></ng-content>
    </div>
  `
})
export class ResizablePanelGroupComponent implements AfterContentInit {
  @Input() direction: 'horizontal' | 'vertical' = 'horizontal';
  @Input() class = '';

  @ContentChildren(ResizablePanelComponent) panels!: QueryList<ResizablePanelComponent>;

  private isDragging = false;
  private currentHandleIndex = -1;

  // Store the initial state when drag starts
  private initialMousePos = 0;
  private initialSizePrev = 0;
  private initialSizeNext = 0;

  // Store sizes for toggle functionality
  private lastSizes: number[] = [];

  constructor(private el: ElementRef) {}

  ngAfterContentInit() {
    const defaultSize = 100 / this.panels.length;
    this.panels.forEach(p => {
      if (!p.size) p.size = defaultSize;
    });
  }

  startResizing(index: number) {
    this.isDragging = true;
    this.currentHandleIndex = index;

    // 1. Capture the Initial State
    const panelsArray = this.panels.toArray();
    this.initialSizePrev = panelsArray[index].size;
    this.initialSizeNext = panelsArray[index + 1].size;

    // 2. Disable transitions globally while dragging
    this.panels.forEach(p => p.isDragging = true);

    // 3. Set cursor style
    document.body.style.cursor = this.direction === 'horizontal' ? 'col-resize' : 'row-resize';
  }

  // We capture the START position here to ensure we have the correct reference
  @HostListener('mousedown', ['$event'])
  onMouseDown(event: MouseEvent) {
    if(this.isDragging) {
      this.initialMousePos = this.direction === 'horizontal' ? event.clientX : event.clientY;
    }
  }

  @HostListener('document:mousemove', ['$event'])
  onMouseMove(event: MouseEvent) {
    if (!this.isDragging || this.currentHandleIndex === -1) return;

    const panelsArray = this.panels.toArray();
    const prevPanel = panelsArray[this.currentHandleIndex];
    const nextPanel = panelsArray[this.currentHandleIndex + 1];

    if (!prevPanel || !nextPanel) return;

    // 1. Calculate how far the mouse has moved (in pixels)
    const currentMousePos = this.direction === 'horizontal' ? event.clientX : event.clientY;
    const deltaPixels = currentMousePos - this.initialMousePos;

    // 2. Convert that pixel delta into a Percentage relative to the container
    const containerRect = this.el.nativeElement.getBoundingClientRect();
    const containerSize = this.direction === 'horizontal' ? containerRect.width : containerRect.height;
    const deltaPercentage = (deltaPixels / containerSize) * 100;

    // 3. Calculate Target Sizes based on the INITIAL stable sizes + delta
    const totalSize = this.initialSizePrev + this.initialSizeNext;

    let newPrevSize = this.initialSizePrev + deltaPercentage;
    let newNextSize = this.initialSizeNext - deltaPercentage;

    // 4. Safety Clamps: Ensure we don't drag past 0 or total size
    // Note: We intentionally allow it to go slightly below minSize here
    // so that the Panel's internal "Snap" logic can trigger.
    if (newPrevSize < 0) newPrevSize = 0;
    if (newNextSize < 0) newNextSize = 0;

    // Prevent dragging next panel below 0
    if (newPrevSize > totalSize) newPrevSize = totalSize;

    // 5. Apply the updates
    prevPanel.updateSize(newPrevSize);

    // The next panel takes whatever is left of the shared space
    const actualPrevSize = prevPanel.isCollapsed ? prevPanel.collapsedSize : prevPanel.size;
    nextPanel.updateSize(totalSize - actualPrevSize);
  }

  @HostListener('document:mouseup')
  onMouseUp() {
    this.isDragging = false;
    this.panels.forEach(p => p.isDragging = false);
    document.body.style.cursor = '';
  }

  // ... togglePanel and handleKeyboardEvent methods remain the same ...
  togglePanel(index: number) {
    const panelsArray = this.panels.toArray();
    const panel = panelsArray[index];
    const sibling = panelsArray[index + 1];

    if (!panel || !sibling) return;

    if (panel.isCollapsed) {
      const restoreSize = this.lastSizes[index] || panel.minSize || 20;
      panel.updateSize(restoreSize);
      sibling.updateSize(sibling.size - (restoreSize - panel.collapsedSize));
    } else {
      this.lastSizes[index] = panel.size;
      const spaceToGive = panel.size - panel.collapsedSize;
      panel.updateSize(0);
      sibling.updateSize(sibling.size + spaceToGive);
    }
  }

  @HostListener('document:keydown', ['$event'])
  handleKeyboardEvent(event: KeyboardEvent) {
    if ((event.ctrlKey || event.metaKey) && event.key.toLowerCase() === 'b') {
      event.preventDefault();
      this.togglePanel(0);
    }
  }

  protected cn = cn;
}

import {
  Component,
  Input,
  forwardRef,
  ElementRef,
  ViewChild,
  OnDestroy,
  HostListener,
  ContentChildren,
  QueryList,
  AfterContentInit
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, FormsModule } from '@angular/forms';
import { computePosition, flip, shift, offset, autoUpdate } from '@floating-ui/dom';
import { cn } from './utils/cn';
import { SelectItemComponent } from './select-item.component';
import { Subscription } from 'rxjs';
import { SelectService } from './select.service';
import { InputComponent } from './input.component';

@Component({
  selector: 'tolle-select',
  standalone: true,
  imports: [CommonModule, FormsModule, InputComponent],
  providers: [
    SelectService,
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => SelectComponent),
      multi: true
    }
  ],
  template: `
    <div [class]="cn('w-full', 'size-' + size)" #container>
      <button
        type="button"
        #trigger
        (click)="toggle()"
        [disabled]="disabled"
        [class]="computedTriggerClass"
      >
        <span class="truncate" [class.text-muted-foreground]="!selectedLabel">
          {{ selectedLabel || placeholder }}
        </span>
        <i [class]="iconClass"></i>
      </button>

      <div
        #popover
        *ngIf="isOpen"
        [ngStyle]="{minWidth: container.clientWidth+'px'}"
        class="absolute bg-popover z-50 min-w-full max-h-[300px] overflow-auto flex flex-col rounded-md border border-border text-popover-foreground shadow-md"
        style="visibility: hidden; top: 0; left: 0;">
        <div *ngIf="searchable" class="p-2 border-b border-border bg-popover h-auto">
          <tolle-input
            size="xs"
            placeholder="Search..."
            [(ngModel)]="searchQuery"
            (ngModelChange)="onSearchChange($event)"
            class="w-full">
            <i prefix class="ri-search-line"></i>
          </tolle-input>
        </div>

        <div class="p-1 overflow-y-auto scrollbar-hidden grow h-full w-full">
          <ng-content></ng-content>
          <div *ngIf="noResults" class="py-6 text-center text-sm text-muted-foreground">
            No results found.
          </div>
        </div>
      </div>
    </div>
  `,
})
export class SelectComponent implements ControlValueAccessor, AfterContentInit, OnDestroy {
  @Input() placeholder = 'Select an option';
  @Input() class = '';
  @Input() disabled = false;
  @Input() searchable = false;
  @Input() size: 'xs' | 'sm' | 'default' | 'lg' = 'default';
  @Input() readonly = false;

  @ViewChild('trigger') trigger!: ElementRef;
  @ViewChild('popover') popover!: ElementRef;
  @ContentChildren(SelectItemComponent, { descendants: true }) items!: QueryList<SelectItemComponent>;

  private sub = new Subscription();
  searchQuery = '';
  noResults = false;
  isOpen = false;
  value: any = null;
  selectedLabel = '';
  cleanupAutoUpdate?: () => void;

  onChange: any = () => {};
  onTouched: any = () => {};

  protected cn = cn;

  constructor(private selectService: SelectService) {
    this.sub.add(
      this.selectService.selectedValue$.subscribe(val => {
        this.value = val;
        this.onChange(val);
        this.updateItemSelection();
      })
    );

    this.sub.add(
      this.selectService.selectedLabel$.subscribe(label => {
        this.selectedLabel = label;
        this.close();
      })
    );
  }

  // UPDATED: Centralized sizing logic for the trigger
  get computedTriggerClass() {
    return cn(
      'flex w-full items-center justify-between rounded-md border transition-all shadow-sm',
      'bg-background ring-offset-background placeholder:text-muted-foreground',

      // Sizing
      this.size === 'xs' && 'h-8 px-2 text-xs',
      this.size === 'sm' && 'h-9 px-3 text-sm',
      this.size === 'default' && 'h-10 px-3 text-sm',
      this.size === 'lg' && 'h-11 px-4 text-base',

      // Interaction States (Focus ring disabled for Readonly/Disabled)
      !(this.readonly || this.disabled) && 'focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-1',

      // Colors & Borders (Matching your Hex-based theme logic)
      this.disabled && 'cursor-not-allowed opacity-50 bg-muted/30 border-input',
      this.readonly && 'cursor-default bg-muted/10 border-dashed border-input focus:ring-0',
      !this.disabled && !this.readonly && 'border-input hover:border-accent cursor-pointer',

      this.class
    );
  }

  // UPDATED: Dynamic icon sizing relative to the trigger size
  get iconClass() {
    return cn(
      'ri-arrow-down-s-line text-muted-foreground ml-2 transition-transform duration-200',
      this.isOpen ? 'rotate-180' : '',
      (this.size === 'xs' || this.size === 'sm') ? 'text-[14px]' : 'text-[18px]',
      // Hide or fade icon when interaction is blocked
      (this.disabled || this.readonly) && 'opacity-30'
    );
  }

  ngAfterContentInit() {
    this.updateItemSelection();
    this.items.changes.subscribe(() => this.updateItemSelection());
  }

  private updateItemSelection() {
    if (this.items) {
      this.items.forEach(item => {
        item.selected = item.value === this.value;
      });
    }
  }

  toggle() {
    if (this.disabled || this.readonly) return;
    this.isOpen ? this.close() : this.open();
  }

  open() {
    this.isOpen = true;
    // Tick to ensure DOM is rendered before positioning
    setTimeout(() => this.updatePosition());
  }

  close() {
    this.isOpen = false;
    this.searchQuery = '';
    this.onSearchChange('');
    if (this.cleanupAutoUpdate) this.cleanupAutoUpdate();
  }

  private updatePosition() {
    if (!this.trigger || !this.popover) return;

    this.cleanupAutoUpdate = autoUpdate(
      this.trigger.nativeElement,
      this.popover.nativeElement,
      () => {
        computePosition(this.trigger.nativeElement, this.popover.nativeElement, {
          placement: 'bottom-start',
          middleware: [offset(4), flip(), shift({ padding: 8 })],
        }).then(({ x, y }) => {
          Object.assign(this.popover.nativeElement.style, {
            left: `${x}px`,
            top: `${y}px`,
            visibility: 'visible',
          });
        });
      }
    );
  }

  onSearchChange(query: string) {
    const filter = (query || '').toLowerCase().trim();
    let visibleCount = 0;

    this.items.forEach(item => {
      const text = item.getLabel().toLowerCase();
      const isVisible = text.includes(filter);
      item.hidden = !isVisible;
      if (isVisible) visibleCount++;
    });

    this.noResults = visibleCount === 0 && filter !== '';
  }

  @HostListener('document:mousedown', ['$event'])
  onDocumentClick(event: MouseEvent) {
    if (this.isOpen && !this.trigger.nativeElement.contains(event.target) && !this.popover.nativeElement.contains(event.target)) {
      this.close();
    }
  }

  writeValue(value: any): void {
    this.value = value;
    this.updateItemSelection();
    if (this.items) {
      const found = this.items.find(i => i.value === value);
      if (found) this.selectedLabel = found.getLabel();
    }
  }

  registerOnChange(fn: any): void { this.onChange = fn; }
  registerOnTouched(fn: any): void { this.onTouched = fn; }
  setDisabledState(isDisabled: boolean): void { this.disabled = isDisabled; }

  ngOnDestroy() {
    this.sub.unsubscribe();
    if (this.cleanupAutoUpdate) this.cleanupAutoUpdate();
  }
}

