import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { cn } from './utils/cn';

@Component({
  selector: 'tolle-skeleton',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div [class]="computedClass"></div>
  `,
})
export class SkeletonComponent {
  @Input() variant: 'rect' | 'circle' | 'pill' = 'rect';
  @Input() class = '';

  protected readonly cn = cn;

  get computedClass() {
    return cn(
      // The background matches the Google Dark Mode "Muted" color
      'animate-pulse bg-muted rounded-md',

      this.variant === 'circle' && 'rounded-full',
      this.variant === 'pill' && 'rounded-full',

      this.class
    );
  }
}

import { Component, Input, forwardRef, ChangeDetectorRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { cn } from './utils/cn';

@Component({
  selector: 'tolle-switch',
  standalone: true,
  imports: [CommonModule],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => SwitchComponent),
      multi: true
    }
  ],
  template: `
    <button
      type="button"
      role="switch"
      [attr.aria-checked]="checked"
      [disabled]="disabled"
      (click)="toggle()"
      [class]="cn(
    'peer inline-flex shrink-0 cursor-pointer items-center rounded-full border-2 border-transparent transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background disabled:cursor-not-allowed disabled:opacity-50',
    sizeClasses.track,
    checked ? 'bg-primary' : 'bg-input',
    class
  )"
    >
  <span
    [class]="cn(
      'pointer-events-none block rounded-full bg-background shadow-lg ring-0 transition-transform',
      sizeClasses.thumb,
      checked ? sizeClasses.translate : 'translate-x-0'
    )"
  ></span>
    </button>
  `
})
export class SwitchComponent implements ControlValueAccessor {
  @Input() class = '';
  @Input() disabled = false;
  @Input() size: 'xs' | 'sm' | 'default' | 'lg' = 'default';

  checked = false;

  onChange: any = () => {};
  onTouched: any = () => {};

  constructor(private cdr: ChangeDetectorRef) {}

  get sizeClasses() {
    const sizes = {
      xs: {
        track: 'h-4 w-7',
        thumb: 'h-3 w-3',
        translate: 'translate-x-3'
      },
      sm: {
        track: 'h-5 w-9',
        thumb: 'h-4 w-4',
        translate: 'translate-x-4'
      },
      default: {
        track: 'h-6 w-11',
        thumb: 'h-5 w-5',
        translate: 'translate-x-5'
      },
      lg: {
        track: 'h-7 w-[3.25rem]',
        thumb: 'h-6 w-6',
        translate: 'translate-x-6'
      }
    };
    return sizes[this.size];
  }

  protected cn = cn;

  toggle() {
    if (this.disabled) return;
    this.checked = !this.checked;
    this.onChange(this.checked);
    this.onTouched();
  }

  // --- ControlValueAccessor ---
  writeValue(value: boolean): void {
    this.checked = value;
    this.cdr.markForCheck();
  }

  registerOnChange(fn: any): void { this.onChange = fn; }
  registerOnTouched(fn: any): void { this.onTouched = fn; }
  setDisabledState(isDisabled: boolean): void { this.disabled = isDisabled; }
}

import { Component, Input, forwardRef, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, FormsModule } from '@angular/forms';
import { cn } from './utils/cn';

@Component({
  selector: 'tolle-textarea',
  standalone: true,
  imports: [CommonModule, FormsModule],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => TextareaComponent),
      multi: true
    }
  ],
  template: `
    <div class="flex flex-col gap-1.5 w-full">
      <label *ngIf="label" [for]="id"
             [class.opacity-50]="disabled"
             class="text-sm font-medium text-foreground leading-none transition-opacity">
        {{ label }}
      </label>

      <div class="relative">
        <textarea
          #textareaElement
          [id]="id"
          [placeholder]="placeholder"
          [disabled]="disabled"
          [readOnly]="readonly"
          [rows]="rows"
          [(ngModel)]="value"
          (input)="handleInput($event)"
          (blur)="onTouched()"
          [class]="textareaClasses"
          [style.resize]="(autoGrow || readonly || disabled) ? 'none' : 'vertical'"
          [style.overflow]="autoGrow ? 'hidden' : 'auto'"
        ></textarea>
      </div>

      <div *ngIf="(showCharacterCount || hint) && !disabled" class="flex justify-between items-center px-1">
        <p *ngIf="hint" class="text-xs text-muted-foreground">{{ hint }}</p>
        <p *ngIf="showCharacterCount" class="text-[10px] uppercase tracking-wider text-muted-foreground ml-auto font-medium">
          {{ value.length || 0 }}{{ maxLength ? ' / ' + maxLength : '' }}
        </p>
      </div>
    </div>
  `
})
export class TextareaComponent implements ControlValueAccessor, AfterViewInit {
  @ViewChild('textareaElement') textareaElement!: ElementRef<HTMLTextAreaElement>;

  @Input() id = `textarea-${Math.random().toString(36).substr(2, 9)}`;
  @Input() label = '';
  @Input() placeholder = '';
  @Input() hint = '';
  @Input() rows = 3;
  @Input() maxLength?: number;
  @Input() showCharacterCount = false;
  @Input() autoGrow = false;
  @Input() error = false;
  @Input() className = '';

  // New States
  @Input() disabled = false;
  @Input() readonly = false;

  value: string = '';
  onChange: any = () => {};
  onTouched: any = () => {};

  ngAfterViewInit() {
    if (this.autoGrow) this.resize();
  }

  get textareaClasses() {
    return cn(
      'flex w-full rounded-md border bg-background px-3 py-2 text-sm ring-offset-background transition-all duration-200 shadow-sm',
      'placeholder:text-muted-foreground focus-visible:outline-none',

      // Focus States (Disabled only when readonly or disabled is true)
      !(this.readonly || this.disabled) && 'focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-1',

      // Border colors
      this.error ? 'border-destructive' : 'border-input',

      // Disabled vs Readonly styles
      this.disabled && 'cursor-not-allowed opacity-50',
      this.readonly && 'cursor-default border-dashed focus-visible:ring-0',

      'scrollbar-thin scrollbar-thumb-muted scrollbar-track-transparent',
      'min-h-[80px]',
      this.className
    );
  }

  handleInput(event: any) {
    if (this.readonly || this.disabled) return;
    const val = event.target.value;
    this.value = val;
    this.onChange(val);
    if (this.autoGrow) this.resize();
  }

  private resize() {
    const textarea = this.textareaElement.nativeElement;
    textarea.style.height = 'auto';
    textarea.style.height = `${textarea.scrollHeight}px`;
  }

  writeValue(value: any): void {
    this.value = value;
    if (this.autoGrow) setTimeout(() => this.resize());
  }
  registerOnChange(fn: any): void { this.onChange = fn; }
  registerOnTouched(fn: any): void { this.onTouched = fn; }
  setDisabledState(isDisabled: boolean): void { this.disabled = isDisabled; }
}

import {Component, inject, Input} from '@angular/core';
import { CommonModule } from '@angular/common';
import {ToastService, Toast, ToastPosition} from './toast.service';
import { cn } from './utils/cn';

@Component({
  selector: 'tolle-toaster',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div [class]="cn('fixed z-[100] flex flex-col gap-2 w-full max-w-[380px] p-4', positionClasses)">
      <div
        *ngFor="let toast of toasts$ | async"
        (mouseenter)="toastService.setPaused(toast.id, true)"
        (mouseleave)="toastService.setPaused(toast.id, false)"
        [class]="cn(
          'relative overflow-hidden p-4 rounded-md border border-border shadow-lg flex items-start justify-between gap-4 transition-all duration-300 bg-background text-foreground',
          toast.variant === 'destructive' && 'border-destructive/50 text-destructive'
        )"
      >
        <div class="grid gap-1">
          <div *ngIf="toast.title" class="text-sm font-semibold">{{ toast.title }}</div>
          <div class="text-xs opacity-90">{{ toast.description }}</div>
        </div>

        <button (click)="toastService.remove(toast.id)" class="opacity-50 hover:opacity-100">
          <i class="ri-close-line"></i>
        </button>

        <div
          class="absolute bottom-0 left-0 h-1 transition-all duration-100 ease-linear"
          [style.width.%]="(toast.remainingTime / (toast.duration || 3000)) * 100"
          [class.bg-destructive]="toast.variant === 'destructive'"
          [class.bg-primary]="toast.variant !== 'destructive'"
        ></div>
      </div>
    </div>
  `
})
export class ToastContainerComponent {
  toastService = inject(ToastService);
  @Input() position: ToastPosition = 'bottom-right';
  toasts$ = this.toastService.toasts$;
  get positionClasses() {
    const pos = {
      'top-right': 'top-0 right-0 flex-col-reverse',
      'top-left': 'top-0 left-0 flex-col-reverse',
      'bottom-right': 'bottom-0 right-0',
      'bottom-left': 'bottom-0 left-0',
      'top-center': 'top-0 left-1/2 -translate-x-1/2 flex-col-reverse',
      'bottom-center': 'bottom-0 left-1/2 -translate-x-1/2'
    };
    return pos[this.position];
  }
  protected cn = cn;
}

import { Meta, StoryObj, moduleMetadata, applicationConfig } from '@storybook/angular';
import { Component, inject, Input } from '@angular/core';
