  decorators: [
    (storyFn, context) => {
      // Get values from globals
      const {
        primaryColor = '#551a65',
        radius = '0.75rem',
        theme = false
      } = context.globals;

      // Convert theme to darkByDefault
      const darkByDefault = theme === 'dark';

      return {
        ...storyFn(),
        applicationConfig: {
          providers: [
            provideZoneChangeDetection({ eventCoalescing: true }),
            provideTolleConfig({
              primaryColor,
              radius,
              darkByDefault
            }),
          ],
        }
      };
    },
  ],
  parameters: {
    controls: {
      matchers: {
       color: /(background|color)$/i,
       date: /Date$/i,
      },
    },
  },

  globalTypes: {
    theme: {
      name: 'Theme Mode',
      description: 'Light or dark mode',
      defaultValue: 'light',
      toolbar: {
        icon: 'contrast',
        items: [
          { value: false, title: 'Light', icon: 'sun' },
          { value: true, title: 'Dark', icon: 'moon' },
        ],
        dynamicTitle: true,
      },
    },
    primaryColor: {
      name: 'Primary Color',
      description: 'Pick your primary brand color',
      defaultValue: '#551a65',
      toolbar: {
        icon: 'paintbrush',
