projects/tolle/.storybook/preview.ts.defaultValue:
{
  decorators: [
    (storyFn, context) => {
      // Get values from globals
      const {
        primaryColor = '#551a65',
        radius = '0.75rem',
        theme = false
      } = context.globals;

      // Convert theme to darkByDefault
      const darkByDefault = theme === 'dark';

      return {
        ...storyFn(),
        applicationConfig: {
          providers: [
            provideZoneChangeDetection({ eventCoalescing: true }),
            provideTolleConfig({
              primaryColor,
              radius,
              darkByDefault
            }),
          ],
        }
      };
    },
  ],
  parameters: {
    controls: {
      matchers: {
       color: /(background|color)$/i,
       date: /Date$/i,
      },
    },
  },

  globalTypes: {
    theme: {
