{
  title: 'UI/Button',
  component: ButtonComponent,
  tags: ['autodocs'],
  // Define controls for the inputs
  argTypes: {
    variant: {
      control: 'select',
      options: ['default', 'destructive', 'outline', 'secondary', 'ghost', 'link'],
      description: 'Visual style of the button',
    },
    size: {
      control: 'select',
      options: ['default', 'xs', 'sm', 'lg', 'icon-xs', 'icon-sm', 'icon', 'icon-lg'],
      description: 'Size dimensions of the button',
    },
    block: {
      control: 'boolean',
      description: 'If true, spans full width',
    },
    disabled: {
      control: 'boolean',
    },
    busy: {
      control: 'boolean',
      description: 'Shows loading spinner and disables interaction',
    }
  },
  // Default args for all stories
  args: {
    variant: 'default',
    size: 'default',
    disabled: false,
    busy: false,
    block: false,
  },
  // Custom render function to handle ng-content
  render: (args) => {
    const { label, ...props } = args as any;
    return {
      props,
      template: `
        <tolle-button
          [variant]="variant"
          [size]="size"
          [block]="block"
          [disabled]="disabled"
          [busy]="busy"
          [class]="class"
        >
         Button
        </tolle-button>
      `,
    };
  },
}

{
  decorators: [
    (storyFn, context) => {
      // Get values from globals
      const {
        primaryColor = '#551a65',
        radius = '0.75rem',
        theme = false
      } = context.globals;

      // Convert theme to darkByDefault
      const darkByDefault = theme === 'dark';

      return {
        ...storyFn(),
        applicationConfig: {
          providers: [
            provideZoneChangeDetection({ eventCoalescing: true }),
            provideTolleConfig({
              primaryColor,
              radius,
              darkByDefault
            }),
          ],
        },
        // Also add theme class to wrapper for CSS variables
        template: `
          <div class="${theme}">
            ${storyFn().template}
          </div>
        `,
      };
    },
  ],
  parameters: {
    controls: {
      matchers: {
       color: /(background|color)$/i,
       date: /Date$/i,
      },
    },
  },

  globalTypes: {
    theme: {
      name: 'Theme Mode',
      description: 'Light or dark mode',
      defaultValue: 'light',
      toolbar: {
        icon: 'contrast',
        items: [
          { value: false, title: 'Light', icon: 'sun' },
          { value: true, title: 'Dark', icon: 'moon' },
        ],
        dynamicTitle: true,
      },
    },
    primaryColor: {
      name: 'Primary Color',
      description: 'Pick your primary brand color',
      defaultValue: '#551a65',
      toolbar: {
        icon: 'paintbrush',
        items: [
          { value: '#551a65', title: 'Purple (#551a65)' },
          { value: '#2563eb', title: 'Blue (#2563eb)' },
          { value: '#ef4444', title: 'Red (#ef4444)' },
          { value: '#059669', title: 'Green (#059669)' },
          { value: '#f59e0b', title: 'Yellow (#f59e0b)' },
          { value: '#8b5cf6', title: 'Violet (#8b5cf6)' },
          { value: '#ec4899', title: 'Pink (#ec4899)' },
          { value: '#000000', title: 'Custom...' },
        ],
        dynamicTitle: true,
      },
    }
  },
}

{
  args: {
    variant: 'default',
    size: 'default',
  },
  render: (args) => ({
    props: args,
    template: `
      <tolle-button [variant]="variant" [size]="size" class="gap-2">
        <svg prefix xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
          <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path>
          <polyline points="22,6 12,13 2,6"></polyline>
        </svg>
        Login with Email
      </tolle-button>
    `,
  }),
}

