import type { Meta, StoryObj } from '@storybook/angular';
import { moduleMetadata, applicationConfig } from '@storybook/angular';
import { Component, Input } from '@angular/core';
import { FormControl, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { provideAnimations } from '@angular/platform-browser/animations';
import { DatePickerComponent } from '../lib/date-picker.component';

// 1. Wrapper Component for Form State
@Component({
  selector: 'date-picker-story-wrapper',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, DatePickerComponent],
  template: `
    <div class="p-10 w-full h-screen flex flex-col justify-content-center items-center">
      <div class="w-full max-w-xs space-y-4">
        <div>
          <label class="text-sm font-medium text-foreground">Pick a Date</label>
          <p class="mb-2 text-xs text-muted-foreground">Manual entry or calendar selection</p>
        </div>

        <tolle-date-picker
          [formControl]="dateControl"
          [placeholder]="placeholder"
          [disabled]="disabled"
          [disablePastDates]="disablePastDates"></tolle-date-picker>

        <div class="bg-muted/30 mt-8 rounded-lg border border-dashed p-4">
          <p class="font-mono text-xs">
            <strong>FormControl Value:</strong><br />
            {{ dateControl.value ? (dateControl.value | date: 'fullDate') : 'null' }}
          </p>
        </div>
      </div>
    </div>
  `,
})
class DatePickerWrapperComponent {
  dateControl = new FormControl<Date | null>(null);

  @Input() placeholder = 'MM/DD/YYYY';
  @Input() disabled = false;
  @Input() disablePastDates = false;
}

// 2. Metadata Configuration
const meta: Meta<DatePickerComponent> = {
  title: 'Components/Date Picker',
  component: DatePickerComponent,
  tags: ['autodocs'],
  decorators: [
    applicationConfig({
      providers: [provideAnimations()],
    }),
    moduleMetadata({
      imports: [DatePickerWrapperComponent, CommonModule, ReactiveFormsModule],
    }),
  ],
  argTypes: {
    placeholder: { control: 'text' },
    disabled: { control: 'boolean' },
    disablePastDates: { control: 'boolean' },
  },
};

export default meta;
type Story = StoryObj<DatePickerComponent>;

// --- Stories ---

export const Interactive: Story = {
  args: {
    placeholder: 'Select a date...',
    disabled: false,
    disablePastDates: false,
  },
  render: args => ({
    props: args,
    template: `
      <date-picker-story-wrapper
        [placeholder]="placeholder"
        [disabled]="disabled"
        [disablePastDates]="disablePastDates">
      </date-picker-story-wrapper>
    `,
  }),
};

export const DisabledState: Story = {
  args: {
    disabled: true,
    placeholder: 'Cannot select date',
  },
  render: args => ({
    props: args,
    template: `
      <div class="p-10 w-full">
        <tolle-date-picker [disabled]="disabled" [placeholder]="placeholder"></tolle-date-picker>
      </div>
    `,
  }),
};

export const CustomPlaceholder: Story = {
  args: {
    placeholder: 'Birthday (MM/DD/YYYY)',
    disabled: true,
  },
  render: args => ({
    props: args,
    template: `
       <div class="p-10 w-full h-screen flex flex-col justify-content-center items-center">
        <tolle-date-picker [placeholder]="placeholder"></tolle-date-picker>
      </div>
    `,
  }),
};

import type { Meta, StoryObj } from '@storybook/angular';
import { moduleMetadata } from '@storybook/angular';
import { Component, Input } from '@angular/core';
import { FormControl, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import {MaskedInputComponent} from '../lib/masked-input.component';

// 1. Wrapper Component for Form Logic
@Component({
  selector: 'masked-story-wrapper',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, MaskedInputComponent],
  template: `
    <div class="p-10 space-y-6 max-w-md bg-background border rounded-xl">
      <h3 class="font-bold text-lg">Input Masking Demo</h3>

      <div class="space-y-1">
        <label class="text-sm font-medium">Phone Number</label>
        <tolle-masked-input
          mask="(000) 000-0000"
          placeholder="(555) 000-0000"
          [returnRaw]="returnRaw"
          [formControl]="phoneControl"
        >
          <i prefix class="ri-phone-line"></i>
        </tolle-masked-input>

        <div class="p-2 bg-muted rounded text-xs font-mono mt-2">
          <strong>Model Value:</strong> "{{ phoneControl.value }}"
        </div>
        <p class="text-xs text-muted-foreground mt-1">
          Type "1234567890". <br>
          Toggle <code>returnRaw</code> in Controls to see the value change.
        </p>
      </div>

      <div class="space-y-1 pt-4 border-t">
        <label class="text-sm font-medium">Credit Card</label>
        <tolle-masked-input
          mask="0000 0000 0000 0000"
          placeholder="0000 0000 0000 0000"
        >
          <i prefix class="ri-bank-card-line"></i>
        </tolle-masked-input>
      </div>
    </div>
  `
})
class MaskedWrapperComponent {
  phoneControl = new FormControl('');

  // Expose input to Storybook controls
  @Input() returnRaw = true;
}

// 2. Metadata Configuration
const meta: Meta<MaskedInputComponent> = {
  title: 'Components/Masked Input',
  component: MaskedInputComponent,
  tags: ['autodocs'],
  decorators: [
    moduleMetadata({
      imports: [MaskedWrapperComponent, CommonModule, ReactiveFormsModule],
    }),
  ],
  argTypes: {
    mask: {
      control: 'text',
      description: 'Pattern string (0=digit, a=letter, *=alphanumeric)',
    },
    returnRaw: {
      control: 'boolean',
      description: 'If true, the FormControl value excludes mask characters.',
    },
    size: {
      control: 'select',
      options: ['xs', 'sm', 'default', 'lg'],
    },
    disabled: {
      control: 'boolean',
    },
    error: {
      control: 'boolean',
    },
  },
};

export default meta;
type Story = StoryObj<MaskedInputComponent>;

// --- Stories ---

// 1. The Interactive Form Playground
export const InteractiveForm: Story = {
  args: {
    returnRaw: true,
  },
  render: (args) => ({
    props: args,
    template: `<masked-story-wrapper [returnRaw]="returnRaw"></masked-story-wrapper>`,
  }),
};

// 3. License Key (Custom Class for Monospace)
export const LicenseKey: Story = {
  args: {
    mask: '****-****-****-****',
    placeholder: 'XXXX-XXXX-XXXX-XXXX',
    class: 'font-mono uppercase',
  },
  render: (args) => ({
    props: args,
    template: `
      <div class="p-10 max-w-sm">
        <label class="text-sm font-medium mb-1 block">Product Key</label>
        <tolle-masked-input
          [mask]="mask"
          [placeholder]="placeholder"
          [class]="class">
          <i prefix class="ri-key-2-line"></i>
        </tolle-masked-input>
      </div>
    `,
  }),
};

// 4. Suffix Usage (Units)
export const WithSuffix: Story = {
  args: {
    mask: '000',
    placeholder: '000',
    size: 'sm',
  },
  render: (args) => ({
    props: args,
    template: `
      <div class="p-10 max-w-[150px]">
        <label class="text-sm font-medium mb-1 block">Weight</label>
        <tolle-masked-input [mask]="mask" [placeholder]="placeholder" [size]="size">
          <span suffix class="text-xs font-bold text-muted-foreground">kg</span>
        </tolle-masked-input>
      </div>
    `,
  }),
};

import type { Meta, StoryObj } from '@storybook/angular';
import { moduleMetadata, applicationConfig } from '@storybook/angular';
import { Component, Input } from '@angular/core';
import {CommonModule} from '@angular/common';
import {ButtonComponent} from '../lib/button.component';
import {ModalService} from '../lib/modal.service'; // Add CommonModule for ngIf

// 1. Define the "Real World" usage code as strings
const TS_CODE = `
import { Component } from '@angular/core';
import { ModalService } from '@tolle_/tolle-ui';

@Component({
  selector: 'app-example',
  templateUrl: './example.html'
})
export class ExampleComponent {
  constructor(private modalService: ModalService) {}

  openModal() {
    this.modalService.open({
      content: 'This is a modal triggered from TypeScript!',
      size: 'normal',
      backdropClose: true,
      center: true
    });
  }
}
`;

const HTML_CODE = `
<tolle-button (click)="openModal()">
  Open Modal
</tolle-button>
`;

// 2. Create a dummy component to actually run in the canvas
@Component({
  selector: 'modal-story-host',
  standalone: true,
  imports: [ButtonComponent, CommonModule], // Add CommonModule
  template: `
    <div class="p-4 flex flex-col gap-4 items-start">
      <tolle-button (click)="open()">Open Modal</tolle-button>
      <div *ngIf="lastEvent" class="text-sm text-muted-foreground">
        Last Event: {{ lastEvent }}
      </div>
    </div>
  `
})
class ModalStoryHost {
  @Input() size: "fullscreen" | "xs" | "sm" | "default" | "lg" = 'default';
  @Input() backdropClose = true;
  @Input() showCloseButton  = true;
  lastEvent = '';

  constructor(private modalService: ModalService) {}

  open() {
    const ref = this.modalService.open({
      content: 'Hello! I am a service-driven modal.',
      size: this.size,
      backdropClose: this.backdropClose,
      showCloseButton: this.showCloseButton
    });

    ref.afterClosed$.subscribe(res => this.lastEvent = `Closed (${res.type})`);
  }
}

// 3. Define Meta
const meta: Meta<ModalStoryHost> = {
  title: 'Components/Modal',
  component: ModalStoryHost,
  tags: ['autodocs'],
  decorators: [
    moduleMetadata({
      imports: [ModalStoryHost], // Ensure the host component itself is imported here!
      providers: [ModalService]
    })
  ],
  argTypes: {
    size: {
      control: 'select',
      options: ["fullscreen" , "xs" , "sm" , "default" , "lg"]
    }
  },
  // This helps clean up the args table to only show relevant controls
  parameters: {
    controls: { include: ['size', 'backdropClose'] }
  }
};

export default meta;
type Story = StoryObj<ModalStoryHost>;

// 4. The Default Story with Combined Source
export const Basic: Story = {
  args: {
    size: 'default',
    backdropClose: true,
    showCloseButton: true
  },
  parameters: {
    docs: {
      source: {
        // This overrides the "Show Code" button content
        code: `
/* --- example.component.ts --- */
${TS_CODE.trim()}

/* --- example.component.html --- */
${HTML_CODE.trim()}
        `,
        language: 'typescript'
      }
    }
  }
};

import { Meta, StoryObj, moduleMetadata } from '@storybook/angular';
import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import {PaginationComponent} from '../lib/pagination.component';
import {SelectComponent} from '../lib/select.component';
import {SelectItemComponent} from '../lib/select-item.component';

// 1. Wrapper to manage dynamic data updates
@Component({
  selector: 'pagination-story-wrapper',
  standalone: true,
  imports: [CommonModule, PaginationComponent],
  template: `
    <div class="p-8 space-y-8 bg-background border rounded-xl">
      <div>
        <h3 class="text-lg font-bold">Data Table Simulation</h3>
        <p class="text-sm text-muted-foreground mb-4">
          Current Page: <strong>{{ page }}</strong> |
          Page Size: <strong>{{ size }}</strong>
        </p>
      </div>

      <tolle-pagination
        [totalRecords]="totalRecords"
        [currentPage]="page"
        [currentPageSize]="size"
        [showPageLinks]="showPageLinks"
        [showPageOptions]="showPageOptions"
        [showCurrentPageInfo]="showCurrentPageInfo"
        [currentPageInfoTemplate]="template"
        (onPageNumberChange)="page = $event"
        (onPageSizeChange)="size = $event"
      ></tolle-pagination>

      <div class="grid grid-cols-5 gap-2 mt-4">
        <div *ngFor="let i of [].constructor(size)" class="h-8 bg-muted animate-pulse rounded"></div>
      </div>
    </div>
  `
})
class PaginationWrapperComponent {
  @Input() totalRecords = 120;
  @Input() showPageLinks = true;
  @Input() showPageOptions = true;
  @Input() showCurrentPageInfo = true;
  @Input() template?: string;

  page = 1;
  size = 10;
}

const meta: Meta<PaginationComponent> = {
  title: 'Components/Pagination',
  component: PaginationComponent,
  tags: ['autodocs'],
  decorators: [
    moduleMetadata({
      imports: [
        CommonModule,
        FormsModule,
        SelectComponent,
        SelectItemComponent,
        PaginationWrapperComponent
      ],
    }),
  ],
  argTypes: {
    totalRecords: { control: { type: 'number', min: 0 } },
    currentPageSize: { control: 'number' },
    showPageLinks: { control: 'boolean' },
    showPageOptions: { control: 'boolean' },
    showCurrentPageInfo: { control: 'boolean' },
  }
};

export default meta;
type Story = StoryObj<PaginationComponent>;

// --- Stories ---

export const Default: Story = {
  args: {
    totalRecords: 150,
    currentPageSize: 10,
  },
