{
  decorators: [
    (storyFn, context) => {
      // Get values from globals
      const {
        primaryColor = '#551a65',
        radius = '0.625rem',
        theme = false
      } = context.globals;

      // Convert theme to darkByDefault
      const darkByDefault = theme === 'dark';

      return {
        ...storyFn(),
        applicationConfig: {
          providers: [
            provideZoneChangeDetection({ eventCoalescing: true }),
            provideTolleConfig({
              primaryColor,
              radius,
              darkByDefault
            }),
          ],
        }
      };
    },
  ],
  parameters: {
    docs: {
      theme: themes.dark,
    },
    controls: {
      matchers: {
